"use strict";

document.addEventListener('DOMContentLoaded', function () {
  // inputmask
  Inputmask().mask(document.querySelectorAll('input'));

  // textarea
  var allTexareas = document.querySelectorAll('textarea');
  if (allTexareas) {
    allTexareas.forEach(function (el) {
      el.style.height = el.setAttribute('style', 'height: ' + el.scrollHeight + 'px');
      el.classList.add('auto');
      el.addEventListener('input', function (e) {
        el.style.height = 'auto';
        el.style.height = el.scrollHeight + 'px';
      });
    });
  }

  // btn to top
  var btnToTop = document.querySelector('.btn-to-top');
  var rootElement = document.documentElement;
  function scrollToTop() {
    rootElement.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  }
  btnToTop.addEventListener('click', scrollToTop);

  // mobile menu
  var mobileMenu = document.getElementById('menu');
  var mobileMenuTrigger = document.getElementById('hamburger-toggle');
  if (mobileMenu && mobileMenuTrigger) {
    mobileMenuTrigger.addEventListener('click', function (event) {
      event.preventDefault();
      if (mobileMenu.classList.contains('is--active')) {
        mobileMenuTrigger.classList.remove('is--active');
        mobileMenu.classList.remove('is--active');
        document.body.classList.remove('scroll-disabled');
      } else {
        mobileMenuTrigger.classList.add('is--active');
        mobileMenu.classList.add('is--active');
        document.body.classList.add('scroll-disabled');
      }
    });
  }

  // language
  var languageBtns = document.querySelectorAll('.c-language__btn');
  if (languageBtns) {
    languageBtns.forEach(function (item) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        var activeLanguage = document.querySelector('.c-language__btn.is--active').dataset.lang;
        document.querySelectorAll('.c-language__btn').forEach(function (child) {
          return child.classList.remove('is--active');
        });
        if (activeLanguage == 'en') {
          document.querySelectorAll('.c-language__btn[data-lang="ru"]').forEach(function (child) {
            return child.classList.add('is--active');
          });
        } else if (activeLanguage == 'ru') {
          document.querySelectorAll('.c-language__btn[data-lang="en"]').forEach(function (child) {
            return child.classList.add('is--active');
          });
        }
      });
    });
  }

  // products gallery slider
  var productsGallerySlider = document.querySelector('.products-gallery__slider .swiper');
  if (productsGallerySlider) {
    var myProductsGallerySlider = new Swiper(productsGallerySlider, {
      slidesPerView: 2,
      spaceBetween: 15,
      loop: true,
      speed: 800,
      breakpoints: {
        0: {
          slidesPerView: 'auto',
          spaceBetween: 9.5
        },
        744: {
          slidesPerView: 'auto',
          spaceBetween: 14.5
        },
        1024: {
          slidesPerView: 2,
          spaceBetween: 15
        }
      }
    });
  }

  // catalog products slider
  var catalogProductsSlider = document.querySelector('.catalog-products__slider .swiper');
  if (catalogProductsSlider) {
    var myCatalogProductsSlider = new Swiper(catalogProductsSlider, {
      spaceBetween: 60,
      slidesPerView: 'auto',
      centeredSlides: true,
      roundLengths: true,
      loop: true,
      loopAdditionalSlides: 30,
      speed: 800,
      breakpoints: {
        0: {
          spaceBetween: 0
        },
        744: {
          spaceBetween: 0
        },
        1024: {
          spaceBetween: 60
        }
      }
    });
  }

  // catalog main slider
  var catalogMainSlider = document.querySelector('.catalog-main__slider .swiper');
  if (catalogMainSlider) {
    var myCatalogMainSlider = new Swiper(catalogMainSlider, {
      slidesPerView: 1,
      loop: true,
      speed: 800,
      watchSlidesProgress: true,
      autoplay: {
        delay: 5000
      },
      pagination: {
        el: '.catalog-main__slider .swiper-pagination',
        type: 'fraction'
      },
      navigation: {
        prevEl: '.catalog-main__slider .swiper-button-prev',
        nextEl: '.catalog-main__slider .swiper-button-next'
      }
    });
  }

  // product card slider
  var productCardSlider = document.querySelector('.product-card__slider .swiper');
  if (productCardSlider) {
    var myProductCardSlider = new Swiper(productCardSlider, {
      slidesPerView: 'auto',
      spaceBetween: 26,
      speed: 800,
      pagination: {
        el: '.product-card__slider .swiper-pagination',
        type: 'bullets',
        clickable: true
      },
      breakpoints: {
        0: {
          direction: 'horizontal',
          spaceBetween: 19
        },
        744: {
          direction: 'horizontal',
          spaceBetween: 28
        },
        1024: {
          direction: 'vertical',
          spaceBetween: 26
        }
      }
    });
  }

  // gallery slider
  var gallerySlider = document.querySelector('.gallery__slider .swiper');
  if (gallerySlider) {
    var myGallerySlider = new Swiper(gallerySlider, {
      slidesPerView: 'auto',
      spaceBetween: 15,
      loop: true,
      speed: 800
    });
  }

  // marquee brand
  var marqueeSplide = document.querySelector('.marquee .splide');
  if (marqueeSplide) {
    var myMarqueeSplide = new Splide(marqueeSplide, {
      type: 'loop',
      drag: false,
      focus: 'center',
      direction: 'rtl',
      gap: '70px',
      autoWidth: true,
      pagination: false,
      arrows: false,
      wheel: false,
      autoScroll: {
        speed: -1,
        pauseOnHover: false,
        pauseOnFocus: false
      },
      breakpoints: {
        743: {
          gap: '30px'
        },
        1023: {
          gap: '40px'
        }
      }
    });
    myMarqueeSplide.mount(window.splide.Extensions);
  }

  // marquee partners
  var marqueePartnersSplide = document.querySelector('.partners__marquee .splide');
  if (marqueePartnersSplide) {
    var myPartnersSplide = new Splide(marqueePartnersSplide, {
      type: 'loop',
      drag: false,
      focus: 'center',
      direction: 'rtl',
      gap: '70px',
      autoWidth: true,
      pagination: false,
      arrows: false,
      wheel: false,
      autoScroll: {
        speed: -1,
        pauseOnHover: false,
        pauseOnFocus: false
      },
      breakpoints: {
        743: {
          gap: '30px'
        },
        1023: {
          gap: '40px'
        }
      }
    });
    myPartnersSplide.mount(window.splide.Extensions);
  }

  // product card
  var productCard = document.querySelector('.product-card');
  var productCardBtn = document.querySelectorAll('.product-card-btn');
  var productCardClose = document.querySelectorAll('.product-card__close');
  if (productCard && productCardBtn && productCardClose) {
    productCard.addEventListener('click', function (event) {
      if (event.target.classList.contains('product-card') || event.target.classList.contains('product-card__wrap')) {
        event.preventDefault();
        productCard.classList.remove('is--active');
        document.body.classList.remove('scroll-disabled');
      }
    });
    productCardBtn.forEach(function (item, i) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        productCard.classList.add('is--active');
        document.body.classList.add('scroll-disabled');
      });
    });
    productCardClose.forEach(function (item) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        productCard.classList.remove('is--active');
        document.body.classList.remove('scroll-disabled');
      });
    });
  }
});