"use strict";

document.addEventListener('DOMContentLoaded', function () {
  // function's
  function bodyFixScroll(status) {
    var scrollPosition = 0;
    var paddingOffset = window.innerWidth - document.body.offsetWidth + 'px';
    var elementsFixed = document.querySelectorAll('.fixed');
    if (status) {
      document.body.classList.add('scroll-disabled');
      scrollPosition = window.pageYOffset;
      document.body.style.overflow = 'hidden';
      document.body.style.position = 'fixed';
      if (document.body.style.top) {
        document.body.style.top = document.body.style.top;
      } else {
        document.body.style.top = '-' + scrollPosition + 'px';
      }
      document.body.style.width = '100%';
      document.body.style.paddingRight = paddingOffset;
      if (elementsFixed) {
        elementsFixed.forEach(function (item) {
          item.style.marginRight = paddingOffset;
        });
      }
    } else {
      document.body.classList.remove('scroll-disabled');
      scrollPosition = Math.abs(parseInt(document.querySelector('body').style.top.replace('px', '')));
      document.body.style.removeProperty('overflow');
      document.body.style.removeProperty('position');
      document.body.style.removeProperty('top');
      document.body.style.removeProperty('width');
      document.body.style.removeProperty('padding-right');
      if (elementsFixed) {
        elementsFixed.forEach(function (item) {
          item.style.removeProperty('margin-right');
        });
      }
      window.scrollTo({
        top: !isNaN(scrollPosition) ? scrollPosition : 0,
        behavior: "instant"
      });
    }
  }

  // inputmask
  Inputmask().mask(document.querySelectorAll('input'));

  // aos
  AOS.init({
    once: true
  });

  // textarea
  var allTexareas = document.querySelectorAll('textarea');
  if (allTexareas) {
    allTexareas.forEach(function (el) {
      el.style.height = el.setAttribute('style', 'height: ' + el.scrollHeight + 'px');
      el.classList.add('auto');
      el.addEventListener('input', function (e) {
        el.style.height = 'auto';
        el.style.height = el.scrollHeight + 'px';
      });
    });
  }

  // btn to top
  var btnToTop = document.querySelector('.btn-to-top');
  var rootElement = document.documentElement;
  function scrollToTop() {
    rootElement.scrollTo({
      top: 0,
      behavior: 'smooth'
    });
  }
  if (btnToTop) {
    btnToTop.addEventListener('click', scrollToTop);
  }

  // mobile menu
  var header = document.getElementById('header');
  var mobileMenu = document.getElementById('menu');
  var mobileMenuTrigger = document.getElementById('hamburger-toggle');
  if (header && mobileMenu && mobileMenuTrigger) {
    mobileMenuTrigger.addEventListener('click', function (event) {
      event.preventDefault();
      if (mobileMenu.classList.contains('is--active')) {
        header.classList.remove('is--menu');
        mobileMenuTrigger.classList.remove('is--active');
        mobileMenu.classList.remove('is--active');
        bodyFixScroll();
      } else {
        header.classList.add('is--menu');
        mobileMenuTrigger.classList.add('is--active');
        mobileMenu.classList.add('is--active');
        bodyFixScroll(1);
      }
    });
  }

  // language
  var languageBtns = document.querySelectorAll('.c-language__btn');
  if (languageBtns) {
    languageBtns.forEach(function (item) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        var activeLanguage = document.querySelector('.c-language__btn.is--active').dataset.lang;
        document.querySelectorAll('.c-language__btn').forEach(function (child) {
          return child.classList.remove('is--active');
        });
        if (activeLanguage == 'en') {
          document.querySelectorAll('.c-language__btn[data-lang="ru"]').forEach(function (child) {
            return child.classList.add('is--active');
          });
        } else if (activeLanguage == 'ru') {
          document.querySelectorAll('.c-language__btn[data-lang="en"]').forEach(function (child) {
            return child.classList.add('is--active');
          });
        }
      });
    });
  }

  // iframe loading
  var contentVideoIframe = document.querySelectorAll('.content-video__media');
  if (contentVideoIframe) {
    contentVideoIframe.forEach(function (item) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        var parent = item.closest('.content-video');
        if (!parent.classList.contains('is--active')) {
          parent.classList.add('is--active');
          var frame = parent.querySelector('iframe');
          var link = frame.dataset.src.indexOf('?') > -1 ? frame.dataset.src + '&autoplay=1' : frame.dataset.src + '?autoplay=1';
          frame.setAttribute('src', link);
          frame.removeAttribute('data-src');
        }
      });
    });
  }

  // products gallery slider
  var productsGallerySlider = document.querySelector('.products-gallery__slider .swiper');
  if (productsGallerySlider) {
    var myProductsGallerySlider = new Swiper(productsGallerySlider, {
      slidesPerView: 2,
      spaceBetween: 15,
      loop: true,
      speed: 800,
      breakpoints: {
        0: {
          slidesPerView: 'auto',
          spaceBetween: 9.5
        },
        744: {
          slidesPerView: 'auto',
          spaceBetween: 14.5
        },
        1024: {
          slidesPerView: 2,
          spaceBetween: 15
        }
      }
    });
  }

  // catalog products slider
  var catalogProductsSlider = document.querySelector('.catalog-products__slider .swiper');
  if (catalogProductsSlider) {
    var myCatalogProductsSlider = new Swiper(catalogProductsSlider, {
      spaceBetween: 60,
      slidesPerView: 1,
      centeredSlides: true,
      roundLengths: true,
      loop: true,
      watchOverflow: true,
      speed: 800,
      pagination: {
        el: '.catalog-products__slider .swiper-pagination',
        type: 'fraction',
        formatFractionCurrent: function formatFractionCurrent(number) {
          return number < 10 ? '0' + number : number;
        },
        formatFractionTotal: function formatFractionTotal(number) {
          return number < 10 ? '0' + number : number;
        }
      },
      navigation: {
        prevEl: '.catalog-products__slider .swiper-button-prev',
        nextEl: '.catalog-products__slider .swiper-button-next'
      },
      breakpoints: {
        0: {
          spaceBetween: 9
        },
        1024: {
          spaceBetween: 60
        }
      }
    });
  }

  // catalog main slider
  var catalogMainSlider = document.querySelector('.catalog-main__slider .swiper');
  if (catalogMainSlider) {
    var myCatalogMainSlider = new Swiper(catalogMainSlider, {
      slidesPerView: 1,
      loop: true,
      speed: 800,
      watchSlidesProgress: true,
      autoplay: {
        delay: 5000
      },
      pagination: {
        el: '.catalog-main__slider .swiper-pagination',
        type: 'fraction',
        formatFractionCurrent: function formatFractionCurrent(number) {
          return number < 10 ? '0' + number : number;
        },
        formatFractionTotal: function formatFractionTotal(number) {
          return number < 10 ? '0' + number : number;
        }
      },
      navigation: {
        prevEl: '.catalog-main__slider .swiper-button-prev',
        nextEl: '.catalog-main__slider .swiper-button-next'
      }
    });
  }

  // product card slider
  var productCardSlider = document.querySelector('.product-card__slider .swiper');
  if (productCardSlider) {
    var myProductCardSlider = new Swiper(productCardSlider, {
      slidesPerView: 'auto',
      mousewheel: {
        releaseOnEdges: true
      },
      spaceBetween: 26,
      speed: 800,
      pagination: {
        el: '.product-card__slider .swiper-pagination',
        type: 'bullets',
        clickable: true
      },
      breakpoints: {
        0: {
          direction: 'horizontal',
          spaceBetween: 19
        },
        744: {
          direction: 'horizontal',
          spaceBetween: 28
        },
        1024: {
          direction: 'vertical',
          spaceBetween: 26
        }
      }
    });
  }

  // gallery slider
  var gallerySlider = document.querySelector('.gallery__slider .swiper');
  if (gallerySlider) {
    var myGallerySlider = new Swiper(gallerySlider, {
      slidesPerView: 'auto',
      spaceBetween: 15,
      loop: true,
      speed: 800
    });
  }

  // marquee brand
  var marqueeSplide = document.querySelector('.marquee .splide');
  if (marqueeSplide) {
    var myMarqueeSplide = new Splide(marqueeSplide, {
      type: 'loop',
      drag: false,
      focus: 'center',
      direction: 'rtl',
      gap: '70px',
      autoWidth: true,
      pagination: false,
      arrows: false,
      wheel: false,
      autoScroll: {
        speed: -1,
        pauseOnHover: false,
        pauseOnFocus: false
      },
      breakpoints: {
        743: {
          gap: '30px'
        },
        1023: {
          gap: '40px'
        }
      }
    });
    myMarqueeSplide.mount(window.splide.Extensions);
  }

  // marquee partners
  var marqueePartnersSplide = document.querySelector('.partners__marquee .splide');
  if (marqueePartnersSplide) {
    var myPartnersSplide = new Splide(marqueePartnersSplide, {
      type: 'loop',
      drag: false,
      focus: 'center',
      direction: 'rtl',
      gap: '70px',
      autoWidth: true,
      pagination: false,
      arrows: false,
      wheel: false,
      autoScroll: {
        speed: -1,
        pauseOnHover: false,
        pauseOnFocus: false
      },
      breakpoints: {
        743: {
          gap: '30px'
        },
        1023: {
          gap: '40px'
        }
      }
    });
    myPartnersSplide.mount(window.splide.Extensions);
  }

  // product card
  var productCard = document.querySelector('.product-card');
  var productCardBtn = document.querySelectorAll('.product-card-btn');
  var productCardClose = document.querySelectorAll('.product-card__close');
  if (productCard && productCardBtn && productCardClose) {
    productCard.addEventListener('click', function (event) {
      if (event.target.classList.contains('product-card') || event.target.classList.contains('product-card__wrap')) {
        event.preventDefault();
        productCard.classList.remove('is--active');
        bodyFixScroll();
      }
    });
    productCardBtn.forEach(function (item, i) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        productCard.classList.add('is--active');
        bodyFixScroll(1);
      });
    });
    productCardClose.forEach(function (item) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        productCard.classList.remove('is--active');
        bodyFixScroll();
      });
    });
  }

  // main animation logo
  var mainImage = document.querySelector('.js-img-animation');
  window.addEventListener('scroll', function () {
    if (mainImage) {
      var mainImageImg = mainImage.querySelector('img');
      var rect = mainImage.getBoundingClientRect();
      var containerHeight = mainImage.offsetHeight;
      if (rect.top < window.innerHeight && rect.bottom > 0) {
        var scrollProgress = Math.min(Math.max((window.innerHeight - rect.top) / (window.innerHeight + containerHeight), 0), 1);
        mainImageImg.style.transform = "scale(".concat(1 - scrollProgress * 0.1, ") translateY(").concat(scrollProgress * -25, "px)");
      }
    }
  });

  // brands circles
  var brandsCard = document.querySelector('.brands-card');
  var brandsCircles = document.querySelectorAll('.brands-card__circle');
  if (brandsCard && brandsCircles) {
    if (window.innerWidth >= 1024) {
      var brandsCardDirections = Array.from(brandsCircles).map(function () {
        return {
          x: Math.random() < 0.5 ? -1 : 1,
          y: Math.random() < 0.5 ? -1 : 1,
          multiplierX: Math.random() * 0.035 + 0.02,
          multiplierY: Math.random() * 0.035 + 0.02
        };
      });
      brandsCard.addEventListener('mousemove', function (e) {
        var containerRect = brandsCard.getBoundingClientRect();
        var mouseX = e.clientX - containerRect.left;
        var mouseY = e.clientY - containerRect.top;
        brandsCircles.forEach(function (circle, index) {
          var circleX = circle.offsetLeft + circle.offsetWidth / 2;
          var circleY = circle.offsetTop + circle.offsetHeight / 2;
          var deltaX = (mouseX - circleX) * brandsCardDirections[index].multiplierX * brandsCardDirections[index].x;
          var deltaY = (mouseY - circleY) * brandsCardDirections[index].multiplierY * brandsCardDirections[index].y;
          circle.style.transform = "translate(".concat(deltaX, "px, ").concat(deltaY, "px)");
        });
      });
    }
  }

  // brands inner circles
  var brandsInnerCard = document.querySelector('.brands-inner__wrap');
  var brandsInnerCircles = document.querySelectorAll('.brands-inner__circle');
  if (brandsInnerCard && brandsInnerCircles) {
    if (window.innerWidth >= 1024) {
      var brandsInnerDirections = Array.from(brandsInnerCircles).map(function () {
        return {
          x: Math.random() < 0.5 ? -1 : 1,
          y: Math.random() < 0.5 ? -1 : 1,
          multiplierX: Math.random() * 0.035 + 0.02,
          multiplierY: Math.random() * 0.035 + 0.02
        };
      });
      brandsInnerCard.addEventListener('mousemove', function (e) {
        var containerRect = brandsInnerCard.getBoundingClientRect();
        var mouseX = e.clientX - containerRect.left;
        var mouseY = e.clientY - containerRect.top;
        brandsInnerCircles.forEach(function (circle, index) {
          var circleX = circle.offsetLeft + circle.offsetWidth / 2;
          var circleY = circle.offsetTop + circle.offsetHeight / 2;
          var deltaX = (mouseX - circleX) * brandsInnerDirections[index].multiplierX * brandsInnerDirections[index].x;
          var deltaY = (mouseY - circleY) * brandsInnerDirections[index].multiplierY * brandsInnerDirections[index].y;
          circle.style.transform = "translate(".concat(deltaX, "px, ").concat(deltaY, "px)");
        });
      });
    }
  }

  // modal
  var modal = document.querySelectorAll('.modal');
  var modalBtn = document.querySelectorAll('.modal-btn');
  var modalClose = document.querySelectorAll('.modal__close');
  var modalTimeout;
  if (modal && modalBtn && modalClose) {
    // modal
    modal.forEach(function (item) {
      item.addEventListener('click', function (event) {
        if (event.target.classList.contains('modal') || event.target.classList.contains('modal__wrap')) {
          event.preventDefault();
          document.querySelectorAll('.modal.is--active').forEach(function (child) {
            return child.classList.remove('is--active');
          });
          bodyFixScroll();
        }
      });
    });

    // modal btn
    modalBtn.forEach(function (item, i) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        var modalID = item.dataset.id;
        if (modalTimeout) {
          clearTimeout(modalTimeout);
        }
        if (productCard) {
          if (productCard.classList.contains('is--active')) {
            productCard.classList.remove('is--active');
          }
        }
        if (item.dataset.mobile) {
          if (window.innerWidth <= 743) {
            if (document.getElementById(modalID)) {
              document.querySelectorAll('.modal.is--active').forEach(function (child) {
                return child.classList.remove('is--active');
              });
              document.getElementById(modalID).classList.add('is--active');
              bodyFixScroll(1);
              if (item.dataset.temp) {
                modalTimeout = setTimeout(function () {
                  document.querySelectorAll('.modal.is--active').forEach(function (child) {
                    return child.classList.remove('is--active');
                  });
                  bodyFixScroll();
                }, 3500);
              }
            }
          }
        } else {
          if (document.getElementById(modalID)) {
            document.querySelectorAll('.modal.is--active').forEach(function (child) {
              return child.classList.remove('is--active');
            });
            document.getElementById(modalID).classList.add('is--active');
            bodyFixScroll(1);
            if (item.dataset.temp) {
              modalTimeout = setTimeout(function () {
                document.querySelectorAll('.modal.is--active').forEach(function (child) {
                  return child.classList.remove('is--active');
                });
                bodyFixScroll();
              }, 3500);
            }
          }
        }
      });
    });

    // modal close
    modalClose.forEach(function (item) {
      item.addEventListener('click', function (event) {
        event.preventDefault();
        document.querySelectorAll('.modal.is--active').forEach(function (child) {
          return child.classList.remove('is--active');
        });
        bodyFixScroll();
      });
    });
  }
});